import React from 'react';
import './Cerez.css'; 

const CookiePolicy = () => {
  return (
    <div className="cookie-policy-container">
      <h1>Cookie Policy</h1>
      <p>

At CepGuide, we use cookies to better serve our visitors.
Cookies are small text files stored on your computer or mobile device when you visit our website.
      </p>
      <h2>What Do Cookies Do?</h2>
      <p>
      Cookies are used to make our website work more efficiently, to improve user experience and
        It is used to personalize content and advertisements for you. Thanks to cookies, our site
        We can understand how you use it and improve our services accordingly.
      </p>
      <h2>Cookie Preferences</h2>
      <p>
      If you do not want to accept cookies, you can delete or block cookies from your browser settings.
        However, this may prevent some functional features of our website from working.
      </p>
      <h2>Our Privacy Policy</h2>
      <p>

Data collected through the use of cookies is processed within the scope of our Privacy Policy.
        For detailed information <a href="/privacy-policy">Our Privacy Policy </a>you can visit.
      </p>
    </div>
  );
};

export default CookiePolicy;
